package com.javabasics;

public class BuclesStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int numero = 1;
		
		while (numero<10) {
			
			System.out.println("El número es:" + numero);
			++numero;
			System.out.println("El valor actual es:" + numero);
		}
		
		
	}

}
